import React from 'react';
import TeamVB from '../../Videos/TeamVB.mp4';
import './Slider.css'

import{
    TemaContainer,
    VdoBG,
    TeamBG,
    TeamContent,
    TeamP,
} from './TeamElements'

const Content = [{
    title: 'Team Visible',
    description1: 'Our main goal is to increase awareness for drinkable water and make a halt for kidney diseases caused by consumption of contaminated water drastically, which will create a generation free from kidney disorders in Sri Lanka.',
    // description2: ' ',
    // description3: '',
    // description4: '',
    // description5: ''
    
},{
    title: 'Impact',
    description1: 'As an impact, this project intends to create a water water quality measuring device which uses a IOT (Internet of Things) device to monitor different attributes in water such as the oxygen level, water level, pH values and the turbidity.',
    // description2: '',
    // description3: '',
    // description4: '',
    // description5: ''

},{
    title: 'Vision ',
    description1: 'To reaffirm and promote all people right to safe drinking water. With a focus on developing partnerships with rural communities throughout Sri Lanka, we intend to effect change at the provincial, and federal levels as well as within civil society and industries involved in the protection and production of public water supplies.',
    // description2: '  ',
    // description3: ' ',
    // description4: '',
    // description5: '  ',
    // description6: ''
    
}];
const delay = 7000;

const Team = () => {

    const [index, setIndex] = React.useState(0);
    const timeOut = React.useRef(null);

    function resetTimeOut(){
        if(timeOut.current){
            clearTimeout(timeOut.current)
        }
    }

    React.useEffect(() => {
        resetTimeOut();
        timeOut.current = setTimeout(() => 
            setIndex((prevIndex) => 
                prevIndex === Content.length - 1 ? 0 : prevIndex + 1
            ),
            delay
        );
        return () => {resetTimeOut();};
        
    },[index]);


    return (
        <>
            <TemaContainer id='about'>
                <TeamBG>
                    <VdoBG autoPlay loop muted src={TeamVB} type='video/mp4'/>
                </TeamBG>
                
                <TeamContent>
                
                    <TeamP>
                        <h1 id='topic'>Safe water protects and save life</h1>
                        <div className='slideshow'>
                            <div className='slideshowsliders' style={{transform:`translate3d(${-index*100}%, 0, 0)`}}>
                                {Content.map((block,index) => (
                                    <div className='slide' key={index} style={{block}}>
                                        <h1>{block.title}</h1>
                                        <br/><br/>
                                        <p>{block.description1}</p>
                                        {/* <p>{block.description1}</p>
                                        <p>{block.description2}</p>
                                        <p>{block.description3}</p>
                                        <p>{block.description4}</p>
                                        <p>{block.description5}</p>
                                        <p>{block.description6}</p> */}
                                    </div>
                                ))}
                            </div>
                            <div className='slideshowdots'>
                                {Content.map((_,idx) => (
                                    <div key={idx} 
                                    className={`slideshowdot ${index === idx ? "active":""}`}
                                    onClick ={() => {
                                        setIndex(idx);
                                    }}
                                    ></div>
                                ))}
                            </div>
                        </div>
                    </TeamP>                
                </TeamContent>     
            </TemaContainer>  
        </>
    )
}

export default Team
